    };
  },
  getters: {
    contacts(state) {
      return state.users;
    },
    loadContact(state) {
      return state.users[0][state.contactIndex];
    },
  },
  mutations: {
    addContacts(state, contact) {
      for(let i = 0; i < contact.length; i++){
        state.users.push(contact[i])
      }
      //state.users.push(contact);
    },
    savedIndex(state, idx) {
      state.contactIndex = idx;
    },
    saveContact(state, contact) {
      const index = state.contactIndex;
      if (index !== -1) {
        state.users[index] = contact;
      }
    },
  },
});

export default store;
