    };
  },
  getters: {
    contacts(state) {
      return state.users;
    },

  },
  mutations: {
    loadContacts(state, contact) {
      for(let i = 0; i < contact.length; i++){
        state.users.push(contact[i])
      }
    },
    updateContacts(state){
      state.users = []
    },
    deleteContact(state, id){
      state.users.slice((contact) => contact.id == id);
    },
    savedIndex(state, idx) {
      state.contactIndex = idx;
    },
    saveContact(state, contact) {
      const index = state.contactIndex;
      if (index !== -1) {
        state.users[index] = contact;
      }
    },
  },
});

export default store;
